import { Button, Image, message, Space, Switch, Table } from 'antd';
import React, { useEffect, useState } from 'react';
import { deleteImage, getImagesByPage } from '../../services/pages/PagesService';
import { useSelector } from 'react-redux';
import { formatDate } from '../../utils/date';
import { DeleteOutlined, DownloadOutlined } from '@ant-design/icons';


export const ImageManager = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const selectedPage = useSelector(state => state.pages.selectedPage);

  const columns = [
    {
      title: 'Image',
      dataIndex: 'url',
      key: 'url',
      render: (item) => {
        return (
          <Image
            width={200}
            height={200}
            src={item}
            preview={true}
            fallback='data:image/png;base64,'
          />);
      },
    },
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
      width: '12%',
    },
    {
      title: 'Time Created',
      dataIndex: 'timeCreated',
      width: '30%',
      key: 'timeCreated',
    },
    {
      title: 'Type',
      dataIndex: 'contentType',
      width: '30%',
      key: 'contentType',
    },
    {
      title: 'Size',
      dataIndex: 'size',
      width: '30%',
      key: 'size',
    },

    {
      title: 'Actions',
      dataIndex: 'action',
      width: '30%',
      key: 'action',
      render: (img) => {
        return (
          <Space
            direction='vertical'
            size='middle'
            style={{ display: 'flex', alignItems: 'center' }}>
            <Button
              icon={<DeleteOutlined />}
            >
              Delete
            </Button>
            <a href={img.downloadUrl}>
              <Button
                type='primary'
                shape='round'
                icon={<DownloadOutlined />}
              >
                Download
              </Button>
            </a>
          </Space>
        );
      },
    },
  ];

  // rowSelection objects indicates the need for row selection
  const rowSelection = {
    onChange: (selectedRowKeys, selectedRows) => {
      console.log(`selectedRowKeys: ${selectedRowKeys}`, 'selectedRows: ', selectedRows);
    },
    onSelect: (record, selected, selectedRows) => {
      console.log(record, selected, selectedRows);
    },
    onSelectAll: (selected, selectedRows, changeRows) => {
      console.log(selected, selectedRows, changeRows);
    },
  };

  function bytesToMegabytes(bytes) {
    return String(Math.floor(bytes / 1048576 * 100) / 100) + ' MB';
  }

  const deleteSelectedImage = async (imageName) => {

    const res = await deleteImage(selectedPage, imageName);
    if (res.type === 'success') {
      message.success('image deleted successfully.');
    } else {
      message.error('image deletion failed.');
    }
  };


  const loadData = async () => {
    if (selectedPage) {
      const res = await getImagesByPage(selectedPage);
      const dataTable = [];
      if (res.type === 'success') {
        message.success('images fetched successfully.');
        if (res.object.length > 0) {
          res.object.forEach(img => {
            const newFilename = img.name.split('/').slice(-1);
            const [, ...originalFilename] = String(newFilename).split('_');
            dataTable.push({
              ...img,
              name: originalFilename.join('_'),
              fullName: newFilename,
              timeCreated: formatDate(img.timeCreated),
              size: bytesToMegabytes(img.size),
              contentType: img.contentType.split('/')[1],
              url: img.url,
              action: img,
            });
          });
        }
        setData(dataTable);
        setLoading(false);
      }
      if (res.type === 'error') {
        message.error('Error while fetching images.');


      }

    }
  };

  useEffect(() => {
    loadData();
  }, [selectedPage]);

  return (
    <>
      <Table
        columns={columns}
        rowSelection={{
          ...rowSelection,
        }}
        dataSource={data}
      />
    </>
  );
};

export default ImageManager;